mapper.select:
SELECT noticcIdx, noticeTitle, noticeDate
FROM NOTICE_TABLE
ORDER BY noticeDate DESC;

SELECT noticeIdx, noticeTitle, noticeSub, noticeDate, noticeModify
FROM NOTICE_TABLE
WHERE NOTICE_IDX = #{noticeIdx}

mapper.update:
UPDATE NOTICE_TABLE
SET NOTICE_TITLE = #{noticeTitle},
    NOTICE_SUB = #{noticeSub},
    NOTICE_MODIFY = #{noticeModify}
WHERE NOTICE_IDX = #{noticeIdx}
