mapper.select:
SELECT COUNT(*)
FROM TARGET_CATEGORY
WHERE TARGET_NAME = #{name}

SELECT COUNT(*)
FROM TARGET_CATEGORY
WHERE NAME = #{name}
AND TARGET_IDX != #{idx}

SELECT COUNT(*)
FROM JOB_CATEGORY
WHERE JOB_NAME = #{name}

SELECT COUNT(*)
FROM JOB_CATEGORY
WHERE NAME = #{name}
AND JOB_IDX != #{Idx}

SELECT COUNT(*)
FROM FIRE_TABLE
WHERE FIRE_NAME = #{fireName}

SELECT COUNT(*)
FROM FIRE_TABLE
WHERE FIRE_NAME = #{fireName}
AND LV_IDX != #{lvIdx}

SELECT COUNT(*)
FROM FIRE_TABLE
WHERE NOT (
MAX_EXP < #{minExp} OR MIN_EXP > #{maxExp}
)

SELECT COUNT(*)
FROM FIRE_TABLE
WHERE NOT (
MAX_EXP < #{minExp} OR MIN_EXP > #{maxExp}
)
AND LV_IDX != #{lvIdx}

SELECT COUNT(*)
FROM CHALL_CATEGORY
WHERE CHALL_NAME = #{challName}
