mapper.select:
SELECT COUNT(*)
FROM USER_REPORT
WHERE STATUS = #{status}

mapper.update:
UPDATE USER_REPORT
SET STATUS = #{status}
WHERE USER_REPORT_IDX = #{userReportIdx}

UPDATE USER_TABLE
SET REPORT_COUNT = REPORT_COUNT + 1
WHERE USER_IDX = #{userIdx}

UPDATE USER_TABLE
SET IS_BANNED = 1
WHERE USER_IDX = #{userIdx}

mapper.select:
SELECT COUNT(*)
FROM PLAN_REPORT r
WHERE r.STATUS = #{status}

mapper.update:
UPDATE PLAN_REPORT
SET STATUS = #{status}
WHERE PLAN_REPORT_IDX = #{planReportIdx}
