mapper.update:
UPDATE NOTICE_TABLE
SET IS_POPUP = #{isPopup},
POPUP_START_DATE = #{popupStartDate},
POPUP_END_DATE = #{popupEndDate}
WHERE NOTICE_IDX = #{noticeIdx}

UPDATE NOTICE_TABLE
SET NOTICE_TITLE = #{noticeTitle},
    NOTICE_SUB = #{noticeSub},
    NOTICE_MODIFY = SYSDATE,
    IS_POPUP = #{isPopup}
WHERE NOTICE_IDX = #{noticeIdx}
