mapper.select:
SELECT NOTICE_IDX, NOTICE_TITLE, NOTICE_SUB, NOTICE_DATE, NOTICE_MODIFY
FROM NOTICE_TABLE
WHERE NOTICE_IDX = #{noticeIdx}

mapper.update:
UPDATE NOTICE_TABLE
SET IS_POPUP = #{isPopup},
POPUP_START_DATE = #{popupStartDate},
POPUP_END_DATE = #{popupEndDate}
WHERE NOTICE_IDX = #{noticeIdx}
