UPDATE QNA_TABLE
SET


        Q_COUNT = Q_COUNT + 1


        A_COUNT = A_COUNT + 1


, QNA_STATUS = #{setStatus}
WHERE QNA_IDX = #{qnaIdx}
AND QNA_STATUS NOT IN

    #{status}

UPDATE QNA_TABLE
SET
    TITLE = #{modifyDTO.title},
    CONTENT = #{modifyDTO.content},
    MODIFIED_AT = SYSDATE,
    CATEGORY = #{modifyDTO.category}
WHERE QNA_IDX = #{modifyDTO.qnaIdx}
AND Q_USER_IDX = #{modifyDTO.qUserIdx}
AND QNA_STATUS IN

    #{status}
