choose.when:
UPDATE SEARCH_LOG
SET SEARCH_DATE = SYSDATE, SEARCH_COUNT = SEARCH_COUNT + 1, IS_DELETED = 0
WHERE LOG_IDX = #{logIdx} AND IS_DELETED = #{isDeleted}

choose.otherwise:
UPDATE SEARCH_LOG
SET SEARCH_DATE = SYSDATE, SEARCH_COUNT = SEARCH_COUNT + 1
WHERE LOG_IDX = #{logIdx} AND IS_DELETED = #{isDeleted}
